mapper.select:
select
	count(*)
from (
	select
		a.*
	from
		fc_exam_signup_area a
		left join fc_exam_signup_user b on a.id = b.area_id
		left join fc_province_city_district c on c.code = a.province
		left join fc_province_city_district d on d.code = a.city
		left join fc_province_city_district e on e.code = a.area
	where
		a.signup_id = #{signupId}

			and a.province = #{province}


			and a.city = #{city}


			and a.area = #{district}


			and (
				(
					#{startTime} > a.start_time
					and #{startTime} < a.end_time
				)
				or
				(
					#{endTime} > a.start_time
					and #{endTime} < a.end_time
				)				
			)


			and a.is_putaway = #{isPutaway}

	group by
		a.id
) t

select a.id, concat(c.name, ' ', d.name, ' ', IF(ISNULL(e.name) = 1, '', concat(' ', e.name))) address, a.start_time startTime, a.end_time endTime, a.max_count count, a.is_putaway isPutaway, count(b.area_id) haveCount from fc_exam_signup_area a left join fc_exam_signup_user b on a.id = b.area_id left join fc_province_city_district c on c.code = a.province left join fc_province_city_district d on d.code = a.city left join fc_province_city_district e on e.code = a.area where a.signup_id = #{signupId}
and a.province = #{province}
and a.city = #{city}
and a.area = #{district}
and ( ( #{startTime} > a.start_time and #{startTime} < a.end_time ) or ( #{endTime} > a.start_time and #{endTime} < a.end_time ) )
and a.is_putaway = #{isPutaway}
group by a.id limit #{offset}, #{rows}

select
	b.*
from
	fc_exam_signup_user a
	inner join fc_exam_signup_area b on a.area_id = b.id
where
	a.signup_id = #{signupId}
	and a.area_id != #{signupAreaId}
	and a.role_id = #{roleId}
	and a.role_type = #{roleType}
