mapper.select:
select count(*) from fc_exam_signup_user a inner join fc_exam_signup b on a.signup_id = b.id inner join fc_exam_signup_area c on a.area_id = c.id left join fc_province_city_district d on d.code = c.province left join fc_province_city_district e on e.code = c.city left join fc_province_city_district f on f.code = c.area where 1 = 1 and a.signup_id = #{signupId}
and a.area_id = #{signupAreaId}
and b.user_name like concat('%', #{userName} , '%')
and a.tell_phone like concat('%', #{tellPhone}, '%')
and a.certificate_number like concat('%', #{certificateNumber}, '%')
and a.examine_type = #{examineType}

select a.id, concat(c.province, c.city, c.area, c.address) area, concat(c.start_time, ' 至 ', c.end_time) examTime, a.user_head photo, a.user_name name, a.education, a.tell_phone phone, a.certificate_number certificateNumber,
case a.examine_type when 0 then '未审核' when 1 then '已审核' when 2 then '已驳回' else '-' end auditStatusTxt, a.examine_type auditStatus, a.role_id roleId, a.role_type roleType, b.course_id courseId from fc_exam_signup_user a inner join fc_exam_signup b on a.signup_id = b.id inner join fc_exam_signup_area c on a.area_id = c.id left join fc_province_city_district d on d.code = c.province left join fc_province_city_district e on e.code = c.city left join fc_province_city_district f on f.code = c.area where 1 = 1 and a.signup_id = #{signupId}
and a.area_id = #{signupAreaId}
and b.user_name like concat('%', #{userName} , '%')
and a.tell_phone like concat('%', #{tellPhone}, '%')
and a.certificate_number like concat('%', #{certificateNumber}, '%')
and a.examine_type = #{examineType}
limit #{offset}, #{rows}
