mapper.select:
select count(*) from fc_discount_coupon_record a left join role_staff_archives b on a.grant_role_id = b.role_id and a.grant_role_type = b.role_type where a.coupon_id = #{couponId}
and a.code like concat('%', #{code}, '%')
and b.name like concat('%', #{grantRoleName}, '%')
and a.status = #{status}
and a.use_time >= #{startTime} and a.use_time < date_add(#{endTime}, interval 1 day)

select a.id, a.code, IFNULL(b.name, '-') grantRoleName, case a.grant_role_type when 1 then '员工' when 2 then '机构' when 3 then '家长' when 4 then '从业者' when 5 then '注册用户' else '-' end grantRoleTypeName, IFNULL(date_format(a.grant_time, '%Y/%m/%d %T'), '-') grantTime, IFNULL(b.tell_phone, '-') grantRolePhone, case a.status when 1 then '未发放' when 2 then '已领取' when 3 then '已核销' else '-' end statusName, IFNULL(date_format(a.use_time, '%Y/%m/%d %T'), '-') useTime from fc_discount_coupon_record a left join role_staff_archives b on a.grant_role_id = b.role_id and a.grant_role_type = b.role_type where a.coupon_id = #{couponId}
and a.code like concat('%', #{code}, '%')
and b.name like concat('%', #{grantRoleName}, '%')
and a.status = #{status}
and a.use_time >= #{startTime} and a.use_time < date_add(#{endTime}, interval 1 day)
limit #{offset}, #{rows}
