mapper.select:
select a.id, a.name courseName, a.name as courseName, a.type as courseType, b.`name` as categoryName, a.course_price as coursePrice, a.course_number as courseNumber, a.renew_price as renewPrice, a.remarks as remarks, a.photo as photo, a.update_time as updateTime, a.speaker, a.attribute from fc_course_archives a left join fc_course_category b on a.category_id = b.id
a.is_putaway = 1
and a.category_id = #{categoryId}

select count(*) from fc_course_archives a left join fc_course_category b on a.category_id = b.id
and a.name like concat('%',#{record.name},'%')
and a.type = #{record.type}
and a.category_id = #{record.categoryId}

select a.id as `id`, a.name as courseName, case a.type when 1 then '免费' when 2 then '付费' when 3 then '认证课程' else '' end as type, b.name as categoryName, a.is_putaway as isPutaway from fc_course_archives a left join fc_course_category b on a.category_id = b.id
and a.name like concat('%',#{record.name},'%')
and a.type = #{record.type}
and a.category_id = #{record.categoryId}
limit #{offset}, #{rows}
