mapper.update:
update fc_order_person_hour
set is_pass = #{isPass,jdbcType=INTEGER},
pass_time = #{passTime}
where order_person_id = #{orderPersonId,jdbcType=INTEGER}
AND hour_id = #{hourId,jdbcType=INTEGER}

mapper.delete:
delete
from
	fc_order_person_hour
where
	order_person_id = #{orderPersonId}
	and hour_id = #{hourId}
