mapper.select:
SELECT a.id, b.`name`, b.sex, b.tell_phone AS tellPhone, a.total_count AS totalCount, a.have_count AS haveCount FROM fc_order_person a LEFT JOIN role_practitioner_archives b ON b.id = a.role_id
AND a.order_id = #{orderId} AND b.`name` LIKE CONCAT('%', #{name}, '%')
LIMIT #{offSet}, #{pageSize}

select
	a.id, a.order_person_id orderPersonId, a.hour_id hourId, a.is_pass isPass
from
	fc_order_person_hour a
inner join fc_order_person b on a.order_person_id = b.id
inner join fc_order c on b.order_id = c.id
where
	b.role_id = #{roleId}
	and b.role_type = #{roleType}
	and c.course_id = #{courseId}
	and c.pay_status = 2
	and a.hour_id = #{hourId}
