mapper.select:
    select 
    
    from role_staff_archives
    where 1=1
	    
	    	and id = #{id,jdbcType=INTEGER}
	    
	    
	    	and user_name = #{userName,jdbcType=VARCHAR}
	    
	    
	    	and tell_phone = #{tellPhone}

select id as id, name as name, tell_phone as tellPhone, role_type as roleType, case when role_type=3 then '家长' when role_type=4 then '从业者' else '' end as roleName from role_staff_archives where role_type = 3 or role_type = 4
and name like CONCAT('%',CONCAT(#{name},'%')) or tell_phone like CONCAT('%',CONCAT(#{name},'%'))
limit #{page} , #{limit}

select count(*) from role_staff_archives a, fc_certificate_archives b where a.id = b.role_id and b.state = 1
and a.name like CONCAT('%',CONCAT(#{name},'%'))
and a.tell_phone like CONCAT('%',CONCAT(#{tellPhone},'%'))
and b.audit_status = #{auditStatus}
and b.certificate_type = #{certificateType}

select a.name, a.tell_phone, a.sex, a.user_name, b.id, b.certificate_type, b.photo, b.audit_status from (select id,name,tell_phone,sex,user_name from role_staff_archives where 1=1
and name like CONCAT('%',CONCAT(#{name},'%'))
and tell_phone like CONCAT('%',CONCAT(#{tellPhone},'%'))
)a , fc_certificate_archives b where a.id = b.role_id and b.state = 1
and b.audit_status = #{auditStatus}
and b.certificate_type = #{certificateType}
order by b.create_time desc limit #{page},#{limit}
