mapper.select:
select 

from role_practitioner_archives
where certificate_type = 1 AND certificate_number = #{certificateNumber,jdbcType=INTEGER}

select a.id as teaId, a.name as name, a.certificate_number as certificateNumber, b.id as cerId, b.grade as grade from role_practitioner_archives a, fc_certificate_archives b where a.id = b.role_id and a.type = 2 and b.certificate_type=1
and a.name = #{name}
and a.certificate_number = #{certificateNumber}
limit 1

select a.id as teaId, a.name as name, a.certificate_number as certificateNumber, case when b.certificate_type = 1 then '孤独症康复教育上岗培训合格证书' when b.certificate_type = 2 then '学历证书' when b.certificate_type = 3 then '认证行为分析治疗室' else '' end as typeName, a.sex as sex, b.id as cerId, b.code as certCode, b.certificate_time as certificateTime from role_practitioner_archives a, fc_certificate_archives b where a.id = b.role_id and a.type = 2 and b.certificate_type = 1
and a.name = #{name}
and a.certificate_number = #{certificateNumber}
limit 1

select a.user_head as head, a.name as name, a.certificate_number as certificate_number, a.grade as source, case when a.institution_address ='个人报考' then '' else a.institution_address end as institution_address, b.id as id, b.certificate_time as certificate_time, case when b.certificate_type=1 then '孤独症康复教育上岗培训合格证书' when b.certificate_type=2 then '学历证书' when b.certificate_type=3 then '认证行为分析治疗室' else '' end as cert_name, b.certificate_type as cert_type, b.photo as cert_photo, b.code as cert_code, b.grade as grade, case when b.certificate_type=1 then '中国残疾人康复协会' else b.test_address end as test_address from role_practitioner_archives a, fc_certificate_archives b where a.id = b.role_id and a.type = 2
and b.certificate_type = 1
and a.id = #{teaId}
